import React, { useState } from "react";
import AgoraRTC from "agora-rtc-sdk-ng";

const App = () => {
  const [inCall, setInCall] = useState(false);
  const [client] = useState(() => AgoraRTC.createClient({ mode: "rtc", codec: "vp8" }));
  const [localAudioTrack, setLocalAudioTrack] = useState(null);

  const APP_ID = "e7f6e9aeecf14b2ba10e3f40be9f56e7"; // ŸáŸÖŸàŸÜ App ID ⁄©Ÿá ÿØÿßÿØ€å
  const CHANNEL = "love-channel"; // ÿ®ÿß€åÿØ ÿ™Ÿà Ÿà ÿπÿ¥ŸÇÿ™ €å⁄©€å ÿ®ÿßÿ¥Ÿá
  const TOKEN = null; // ÿ™ÿ≥ÿ™€å ŸÖ€å‚Äåÿ™ŸàŸÜŸá null ÿ®ÿßÿ¥Ÿá

  const joinCall = async () => {
    await client.join(APP_ID, CHANNEL, TOKEN, null);

    // ⁄Øÿ±ŸÅÿ™ŸÜ ŸÖ€å⁄©ÿ±ŸàŸÅŸàŸÜ
    const micTrack = await AgoraRTC.createMicrophoneAudioTrack();
    setLocalAudioTrack(micTrack);

    // ÿßŸÜÿ™ÿ¥ÿßÿ± ÿµÿØÿß
    await client.publish([micTrack]);

    // ŸæÿÆÿ¥ ÿµÿØÿß€å ÿ∑ÿ±ŸÅ ŸÖŸÇÿßÿ®ŸÑ
    client.on("user-published", async (user, mediaType) => {
      await client.subscribe(user, mediaType);
      if (mediaType === "audio") {
        const remoteAudioTrack = user.audioTrack;
        remoteAudioTrack.play();
      }
    });

    setInCall(true);
  };

  const leaveCall = async () => {
    if (localAudioTrack) {
      localAudioTrack.stop();
      localAudioTrack.close();
    }
    await client.leave();
    setInCall(false);
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#ffe6f0",
        flexDirection: "column",
      }}
    >
      {inCall ? (
        <>
          <h2 style={{ color: "#ff4d88" }}>üìû ÿØÿ± ÿ≠ÿßŸÑ ÿ™ŸÖÿßÿ≥ ÿ®ÿß ÿπÿ¥ŸÇÿ™ üíñ</h2>
          <button
            onClick={leaveCall}
            style={{
              padding: "12px 24px",
              borderRadius: "25px",
              background: "#ff4d88",
              color: "white",
              border: "none",
              cursor: "pointer",
              marginTop: "20px",
            }}
          >
            ‚ùå ŸÇÿ∑ÿπ ÿ™ŸÖÿßÿ≥
          </button>
        </>
      ) : (
        <button
          onClick={joinCall}
          style={{
            padding: "15px 30px",
            borderRadius: "30px",
            background: "#ff4d88",
            color: "white",
            fontSize: "18px",
            border: "none",
            cursor: "pointer",
            boxShadow: "0px 4px 10px rgba(0,0,0,0.2)",
          }}
        >
          ‚ù§Ô∏è ÿ¥ÿ±Ÿàÿπ ÿ™ŸÖÿßÿ≥ ÿ®ÿß ÿπÿ¥ŸÇŸÖ ‚ù§Ô∏è
        </button>
      )}
    </div>
  );
};

export default App;
