// src/App.js
import React, { useState } from "react";
import AgoraUIKit from "agora-react-uikit";

const App = () => {
  // حالت اتصال یا قطع تماس
  const [videoCall, setVideoCall] = useState(false);

  // مشخصات Agora
  const rtcProps = {
    appId: "e7f6e9aeecf14b2ba10e3f40be9f56e7", // همون App ID که دادی
    channel: "love-channel", // هر اسمی که تو و عشقت یکی بذارید
    token: null, // چون حالت تست داری
  };

  // تنظیمات UI
  const callbacks = {
    EndCall: () => setVideoCall(false),
  };

  return (
    <div style={{ height: "100vh", display: "flex", justifyContent: "center", alignItems: "center", background: "#ffe6f0" }}>
      {videoCall ? (
        <AgoraUIKit rtcProps={rtcProps} callbacks={callbacks} />
      ) : (
        <button
          onClick={() => setVideoCall(true)}
          style={{
            padding: "15px 30px",
            borderRadius: "30px",
            background: "#ff4d88",
            color: "white",
            fontSize: "18px",
            border: "none",
            cursor: "pointer",
            boxShadow: "0px 4px 10px rgba(0,0,0,0.2)"
          }}
        >
          ❤️ شروع تماس با عشقم ❤️
        </button>
      )}
    </div>
  );
};

export default App;
